export const getMockTransactionReceipt = (hash: string) => {
	console.log(hash);

	const gasUsedHex = "0x5208"; // = 21000
	const gasPriceHex = "0x52a4b7bb"; // = 1384036859

	const gasUsed = hexToBigInt(gasUsedHex); // 21000n
	const gasPrice = hexToBigInt(gasPriceHex); // 1384036859n

	const txFeeWei = gasUsed * gasPrice; // 29064774039000n
	const txFeeEth = Number(formatEther(txFeeWei)); // "0.000029064774039"

	return {
		txFeeWei,
		txFeeEth,
	};
};
